function calc(date) {
  if (typeof date !== "string") {
    throw new Error("Invaild input");
  }
  const today = new Date();
  const birthday = new Date(date);
  if (birthday == "Invalid Date") return "Invaild input";

  return Math.floor((today - birthday) / (1000 * 3600 * 24));
}

module.exports = { calc };
