const { expect } = require("chai");
const { calc } = require("./calc");

describe("Days-since-birth", () => {
  it("should be a function", () => {
    expect(typeof calc).to.equal("function");
  });

  it("should only accept string as an input", () => {
    const invaildInput = calc.bind(null, 216);
    expect(invaildInput).to.throw();
  });

  it("should tell input is invaild", () => {
    const invaildInput = "Hello";
    expect(calc(invaildInput)).to.equal("Invaild input");
  });

  it("should return days since your birthday", () => {
    const birthday = "02/16/1996";
    const today = new Date();
    const days = Math.floor((today - new Date(birthday)) / (1000 * 3600 * 24));
    expect(calc(birthday)).to.equal(days);
  });
});
